import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';





import Button from 'react-bootstrap/Button';

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
        Horrned Beast
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>.
      <image variant="top" src={thisprops.imgUrl}  alt={`${this.props.name}`} title={`${this.props.name}`} />
        <h4>    {this.props.name}</h4>
        <p>
        {this.props.description}
        </p>
      </Modal.Body>

    </Modal>
  );
}



class HornedBeasts extends React.Component{
    constructor(props){
        super(props);

        this.state={
            votes:0,
        }

    }
    incVote=()=>{
        this.setState({votes:this.state.votes+1});
    }
  render() {



    return (
<Card onClick={MyVerticallyCenteredModal} >
  <Card.Img variant="top" src={this.props.imgUrl}  alt={`${this.props.name}`} title={`${this.props.name}`} />
  <Card.Body>
    <Card.Title >{this.props.name}</Card.Title>
    <Card.Text>
    {this.props.description}
    </Card.Text>
    <Card.Text>
    {this.state.votes} ðŸ¤©
    </Card.Text>
    <Button onClick={this.incVote}>like</Button>
  </Card.Body>
  </Card >   
    )
};
}
export default HornedBeasts;
