import React from 'react';
import Footer from './component/Footer';
import Header from './component/Header';
import Main from './component/Main';

import SelectedBeast from './component/SelectedBeast';
import data from './sets/data.json';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state={
      element:data,
      show:false,
      obj:{}
    };
  }
  showElement=(event)=>{
    this.setState({
      show:true,
      obj:event,
    });
    console.log(event);
  }
dontShowElem=()=>{
  this.setState({
    show:false,
  });
}
filterBeast = (allBeast) => {
  if (allBeast === "all") {
    return this.setState({ hornValue: null })
    } else {
      this.setState({ hornValue: allBeast })
    }
  }
render() {
  return (
    <div>
      <Header />

      <Main data={this.state.element} obj={this.state.obj} sowModal={this.showElement}/>

      <SelectedBeast show={this.state.show}
        dontShow={this.dontShowElem}
        obj={this.state.obj}/>
      <Footer />
    </div>
  );
}
}

export default App;
