import React from 'react';
import CardColumns from 'react-bootstrap/CardColumns';
import SelectedBeast from './SelectedBeast';


import Cards from './Cards';

class Main extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      show: false,

    };

  };
    handelM = () => {
      this.setState({ show: !this.state.show });
    };
    render() {
      return <>
        <CardColumns>
          <Cards elementData={this.props.mainData} openM={this.props.this.state.show} />
        </CardColumns>
        <SelectedBeast show={this.state.show} nnot={this.handelM }/>
      </>;
    }
}







export default Main;
