import Form from 'react-bootstrap/Form';


class OurForm extends React.Component {

  handleForm = event => {
    if (event.target.value === 'All') {
      return this.props.filterBeast('all');
    } else {

      let hornArr = this.props.allBeast.filter(beast => beast.horns ===event.target.value);
      this.props.filterBeast(hornArr);

    }
  }





  render() {
    return (<><Form onChange={this.choseHorn}>
      <Form.Group controlId="exampleForm.ControlSelect1">
        <Form.Label>select Number of Horns</Form.Label>
        <Form.Control as="select" onChange={this.handleForm}>

          <option value='1' >1</option>
          <option value='2'>2</option>
          <option value='3'>3</option>
          <option value='4'>100</option>

        </Form.Control>
      </Form.Group>
    </Form>
    </>);
  };
}

export default OurForm;
