import React from 'react';

import Form from 'react-bootstrap/Form';


class OurForm extends React.Component {

  handleForm = event => {
    if (event.target.value === 'All') {
      return this.props.filterBeast('all');
    } else {
      const hornValue = +event.target.value;
      const hornArr = this.props.allBeast.filter(beast => beast.horns === hornValue);
      this.props.filterBeast(hornArr);
    }
  }

  choseHorn = (event) => {
    console.log(event);

    let arrayElem = [];
    this.state.elementData.filter(i => {

      if (Number(event.target.value) === i.horns) {
        arrayElem.push(i);


      } else if (event.target.value === 'All') {
        arrayElem.push(i);
      }
      return arrayElem;
    });

    this.setState({ elementData: arrayElem });

  };

  render() {
    return (console.log('this is ob ',this.props.obj.horns), <><Form onChange={this.choseHorn}>
      <Form.Group controlId="exampleForm.ControlSelect1">
        <Form.Label>select Number of Horns</Form.Label>
        <Form.Control as="select" onChange={this.choseHorn}>
          <option value='ALL'>All</option>
          <option value='1' >1</option>
          <option value='2'>2</option>
          <option value='3'>3</option>
          <option value='4'>100</option>

        </Form.Control>
      </Form.Group>
    </Form>
    </>);
  };
}

export default OurForm;
