import React from 'react';

import Form from 'react-bootstrap/Form';


class OurForm extends React.Component{
  constructor(props){
    super(props);

    this.state={
      obj:this.props.obj,
    };
  }
   tagChanged = (event) => {
     let tagDdlValue = event.value;

     //filter the results based on the value of tags dropdonw
     let optionsToAdd =this.state.obj.filter(item => item.horns === tagDdlValue);

     return optionsToAdd;
   }

   render(){


     return(
       <Form >
         <Form.Group controlId="exampleForm.ControlSelect1">
           <Form.Label>Example select</Form.Label>
           <Form.Control as="select" onClick={this.tagChanged}>
             <option>All</option>
             <option>1</option>
             <option>2</option>
             <option>3</option>
             <option>100</option>

           </Form.Control>
         </Form.Group>
       </Form>);
   }
}

export default OurForm;
