import React from 'react';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';

class SelectedBeast extends React.Component {



  render() {
    return (
      <Modal
        show={this.props.show} dontShow={this.props.dontShow}
      >
        <Modal.Header closeButton onClick={this.props.dontShow}>
          <Modal.Title id="example-custom-modal-styling-title">
          Horrned Beast
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Card >

            <Card.Img variant="top" src={this.props.obj.image_url} alt={this.props.obj.title} title={this.props.obj.title} />

            <Card.Body>
              <Card.Title >{this.props.obj.title}</Card.Title>
              <Card.Text>
                {this.props.obj.description}
              </Card.Text>
            </Card.Body>
          </Card >
        </Modal.Body>
      </Modal>
    );
  }
}

export default SelectedBeast;
