import React from 'react';
import CardColumns from 'react-bootstrap/CardColumns';


import Cards from './Cards';

class Main extends React.Component {
  constructor(props){
    super(props);
    this.state={
      show:false
    };
  };
  showNotShow(){
    this.setState({show:!this.state.show,});
  };


  render() {
    return <div>
      <CardColumns>
        <Cards elementData={this.mainData} showModal={this.showNotShow()}/>
      </CardColumns>
      <SelectedBeast show={this.showNotShow()}/>
    </div>;
  }
}
export default Main;
