    this.state = {
        numberOfPets: 0
    }
}

increaseNumberOfPets = () => {
    this.setState(
        {
            numberOfPets: this.state.numberOfPets + 1
        }
    );
}
render() {
    return (
        <div>
            <Card style={{ width: '18rem' }}/>
                <Card.Img onClick={this.increaseNumberOfPets} variant="top" src={this.props.image_url} />
                <Card.Body>
                    <Card.Title>{this.props.title}</Card.Title>
                    <Card.Text>
                        {this.props.title}
                    </Card.Text>
                    <Card.Text>
                        {this.props.description}
                    </Card.Text>
